(function ($) {
    "use strict";
  
    // Smooth scrolling using jQuery easing
    $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function () {
      // Check if the clicked link's pathname matches the current page's pathname and the hostname matches
      if (
        location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") &&
        location.hostname == this.hostname
      ) {
        var target = $(this.hash);
        target = target.length ? target : $("[name=" + this.hash.slice(1) + "]");
        // Check if the target element exists
        if (target.length) {
          // Animate scrolling to the target element's top position
          $("html, body").animate(
            {
              scrollTop: target.offset().top,
            },
            1000, // Animation duration in milliseconds
            "easeInOutExpo" // Easing effect
          );
          return false; // Prevent default link behavior
        }
      }
    });
  
    // Closes responsive menu when a scroll trigger link is clicked
    $(".js-scroll-trigger").click(function () {
      $(".navbar-collapse").collapse("hide");
    });
  
    // Activate scrollspy to add active class to navbar items on scroll
    $("body").scrollspy({
      target: "#sideNav", // Target element to track the scroll position
    });
  })(jQuery);
  